INSERT INTO `jsen`.`photo` (`work_id`, `photo_image_url`, `photo_title`, `photo_content`)VALUES('h05073698','lvimg.jp/job-haken/img/company/34/134/39339_small.jpg/180x135-f1','未公開求人多数！','ネットで掲載していないお仕事がたくさんあるんです！<br>あなたのご希望条件(勤務地・ブランド・期間・社員切替‥)に合わせてご提案しています♪<br>スムーズにお仕事スタート出来るよう私たちも頑張ります！！</br></br>');
