INSERT INTO `jsen`.`photo` (`work_id`, `photo_image_url`, `photo_title`, `photo_content`)VALUES('h05074382','lvimg.jp/job-haken/img/company/34/134/40016_small.jpg/180x135-f1','男性も女性も歓迎！','メンズもレディスも取り扱っているから、スタッフさんは男性も女性も歓迎！<br>スタッフさんとチームワークを大切にお仕事を進めましょう♪♪</br>');
